mapper.select:
SELECT tt.*, rownum FROM (SELECT a.*, b.rate prediction FROM (SELECT i.movie_num,i.title,i.country,i.released,i.director,i.actors,i.poster_img,i.banner_img,i.main_genre,i.sub_genre,r.id FROM movie_info i FULL OUTER JOIN (SELECT id,movie_num,rate FROM movie_rated WHERE id=#{id})r ON i.movie_num=r.movie_num WHERE rate is null)a RIGHT OUTER JOIN (SELECT genre, ROUND(AVG(rate),1) rate FROM analysis_genre GROUP BY genre)b ON a.main_genre=b.genre
a.main_genre=#{keyword} OR a.sub_genre=#{keyword}
a.director like '%' || #{keyword} || '%'
a.actors like '%' || #{keyword} || '%'
ORDER BY DBMS_RANDOM.VALUE)tt
WHERE rownum <=#{count};
